INSERT INTO `bz_problem` VALUES (14738, '2019-07-17 08:25:17', '2019-07-17 08:25:17', 20, '48.由于在极滑跑道、大速度下具有的刹车摩擦系数低，飞行员会认为是防滞刹车失效，应该意识到（ ）会减小刹车效应。稳定地增加刹车压力，可使防滞系统发挥其最佳性能。', 48, 0);
